import React, { useState } from 'react';
import './App.css'; // Importa el archivo CSS
import { useNavigate } from 'react-router-dom';
import { Bar } from "react-chartjs-2";
import casaAtras from "./Imagenes/CasaNegra.png";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarController,
  BarElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  Title,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  BarController
);

function Resultado() {
  const data = {
    labels: ['Label 1', 'Label 2', 'Label 3', 'Label 4', 'Label 5'],
    datasets: [
      {
        label: 'Data Series 1',
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
        data: [12, 19, 3, 5, 2],
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const navigate = useNavigate();
  const [selectedMedicine, setSelectedMedicine] = useState('a');

  const handleMedicineClick = (label) => {
    setSelectedMedicine(label);
  };

  const goBack = () => {
    navigate(process.env.PUBLIC_URL + '/');
  };

  return (
    <>
      <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/css/bootstrap.min.css' />
      <script src='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/js/bootstrap.bundle.min.js'></script>

      <section className="credit-card bg-news2 d-flex flex-column align-items-center" style={{opacity: selectedMedicine ? 0.4 : 1 }}>
        <div className="d-flex justify-content-end align-items-center w-100 mr-5">
          <img src={casaAtras} alt="Volver" onClick={goBack} style={{ cursor: 'pointer' }} />
        </div>
        <div className="container">
          <h2 className="text-center mb-5" style={{ fontFamily: 'Abhaya Libre', fontWeight: 'bold', letterSpacing: '1px' }}>PREDICTOR DE MEDICAMENTO EFECTIVO</h2>

          <div className="card-holder">
            <div className="card-box" style={{ backgroundColor: 'white' }}>
              <div className="row">
                <div className="col-lg-12" style={{ width: '150vh', height: '60vh' }}>
                  <Bar
                    data={data}
                    options={options}
                    onElementsClick={(elems) => {
                      if (elems.length > 0) {
                        const index = elems[0].index;
                        const label = data.labels[index];
                        handleMedicineClick(label);
                      }
                    }}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <div className="d-flex flex-column align-items-center z-index:5 "style={{position:'absolute',top:'100px',left:'300px'}}>
        {selectedMedicine && (
          <div className="card p-3">
            <h4>Detalles de {selectedMedicine}</h4>
            {/* Agrega aquí los detalles del medicamento seleccionado */}
          </div>
        )}
      </div>
    </>
  );
}

export default Resultado;
