import React, { useState } from 'react';
import './App.css'; // Importa el archivo CSS
import { useNavigate } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';

// Datos de ejemplo para el gráfico
const data = {
  labels: ['Medicamento 1', 'Medicamento 2', 'Medicamento 3', 'Medicamento 4', 'Medicamento 5'],
  datasets: [
    {
      label: 'Efectividad',
      backgroundColor: 'rgba(75,192,192,0.2)',
      borderColor: 'rgba(75,192,192,1)',
      borderWidth: 1,
      hoverBackgroundColor: 'rgba(75,192,192,0.4)',
      hoverBorderColor: 'rgba(75,192,192,1)',
      data: [65, 59, 80, 81, 56]
    }
  ]
};

function Resultado() {
  const navigate = useNavigate();
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  const handleMedicineClick = (label) => {
    setSelectedMedicine(label);
  };

  return (
    <>
      <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/css/bootstrap.min.css'/>
      <script src='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/js/bootstrap.bundle.min.js'></script>

      <section className="credit-card">
        <div className="container d-flex flex-column align-items-center">
          <h2 className="text-center mb-4" style={{ fontFamily: 'Abhaya Libre', fontWeight: 'bold', letterSpacing: '1px' }}>PREDICTOR DE MEDICAMENTO EFECTIVO</h2>

          <div className="card-holder">
            <div className="card-box ">
              <div className="row">
                <div className="col-md-6">
                <Bar
                    data={{
                        // Name of the variables on x-axies for each bar
                        labels: ["1st bar", "2nd bar", "3rd bar", "4th bar"],
                        datasets: [
                            {
                                // Label for bars
                                label: "total count/value",
                                // Data or value of your each variable
                                data: [1552, 1319, 613, 1400],
                                // Color of each bar
                                backgroundColor: 
                                    ["aqua", "green", "red", "yellow"],
                                // Border color of each bar
                                borderColor: ["aqua", "green", "red", "yellow"],
                                borderWidth: 0.5,
                            },
                        ],
                    }}
                    // Height of graph
                    height={400}
                    options={{
                      maintainAspectRatio: false,
                      scales: {
                        yAxes: [{
                          ticks: {
                            beginAtZero: true
                          }
                        }]
                      }
                    }}
                    
                />
                </div>
                <div className="col-md-6">
                  {selectedMedicine && (
                    <div className="card p-3">
                      <h4>Detalles de {selectedMedicine}</h4>
                      {/* Agrega aquí los detalles del medicamento seleccionado */}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Resultado;
