import React, { useState } from 'react';
import './App.css'; // Importa el archivo CSS
import { useNavigate } from 'react-router-dom';
import { Bar } from "react-chartjs-2";
import casaAtras  from "./Imagenes/CasaNegra.png";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarController,
  BarElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  Title,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
  BarController
);


function Resultado() {

  const data = {
    labels: ['Label 1', 'Label 2', 'Label 3', 'Label 4', 'Label 5'],
    datasets: [
      {
        label: 'Data Series 1',
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
        data: [12, 19, 3, 5, 2],
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  const navigate = useNavigate();
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  const handleMedicineClick = (label) => {
    setSelectedMedicine(label);
  };
  const goBack = () => {
    navigate('/')
  };

  return (
    <>
      <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/css/bootstrap.min.css'/>
      <script src='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/js/bootstrap.bundle.min.js'></script>

      <section className="credit-card bg-news">
        <div className="container d-flex flex-column align-items-center">
          <h2 className="text-center mb-4" style={{ fontFamily: 'Abhaya Libre', fontWeight: 'bold', letterSpacing: '1px' }}>PREDICTOR DE MEDICAMENTO EFECTIVO</h2>
          <img src={casaAtras} alt="Volver" onClick={goBack} style={{ cursor: 'pointer' }} />

          <div className="card-holder">
          <div className="card-box" style={{backgroundColor:'white'}}>
            <div className="row">
                <div className="col-lg-12" style={{width:'150vh', height:'60vh'}}>
                  <Bar data={data} options={options} />
                </div>
                <div className="col-lg-6 ">
                  {selectedMedicine && (
                    <div className="card p-3">
                      <h4>Detalles de {selectedMedicine}</h4>
                      {/* Agrega aqu√≠ los detalles del medicamento seleccionado */}
                    </div>
                  )}
                </div>
            </div>
            </div>
            </div>
        </div>
      </section>
    </>
  );
}

export default Resultado;
