import React, { useState } from 'react';
import './App.css'; // Importa el archivo CSS
import Select from 'react-select';
import Virus from './Imagenes/sick-virus.gif';
import { useNavigate } from 'react-router-dom';


function Formulario() {
  const navigate = useNavigate();

  const enfOptions = [
    { value: 'none', label: 'Selecciona una enfermedad' },
    { value: 'red', label: 'Red' },
    { value: 'green', label: 'Green' },
    { value: 'blue', label: 'Blue' },
    { value: 'yellow', label: 'Yellow' },
    { value: 'orange', label: 'Orange' },
  ];

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      fontWeight: state.isSelected ? 'normal' : 'normal',
    }),
    singleValue: (provided, state) => ({
      ...provided,
      fontWeight: 'normal',
    }),
  };

  // Estado para almacenar los valores de los campos del formulario
  const [formData, setFormData] = useState({
    enfermedad: '',
    edad: '',
    peso: '',
    sexo: '',
  });

  // Función para manejar el cambio en los campos del formulario
  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.id]: e.target.value,
    });
  };

  // Función para manejar el envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    // Comprueba si todos los campos están completos antes de redirigir
    if (formData.enfermedad && formData.edad && formData.peso && formData.sexo) {
      // Redirige a otra página
      navigate('/resultado');
    } else {
      // Si falta algún campo, muestra un mensaje de error o toma alguna otra acción
      console.log('Por favor, completa todos los campos.')
      alert('Por favor, completa todos los campos.');
    }
  };
  return (
    <>
    <link rel='stylesheet' href='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/css/bootstrap.min.css'/>
    <script src='https://cdn.jsdelivr.net/npm/bootstrap@4.3.1/dist/js/bootstrap.bundle.min.js'></script>

    <section className="credit-card">
		  <div className="container">
      <h2 className="text-center mb-4" style={{fontFamily: 'Abhaya Libre', fontWeight:'bold', letterSpacing: '1px'}}>PREDICTOR DE MEDICAMENTO EFECTIVO</h2>
      <div className="card-holder">
          <div className="card-box bg-news">
            <div className="row">

              <div className="col-lg-6">
                <div className="img-box">
                  {/* <img src={Virus} alt="GIF" className="img-fluid" style={{ width: '50%', marginLeft:'50px' }}/> */}
                  <img src="https://bootdey.com/img/Content/avatar/avatar7.png" className="img-fluid" alt="Avatar" />

                </div>
              </div>

              <div className="col-lg-6">
                <form>
                  <div className="card-details">
                    <h3 className="title">DATOS DEL PACIENTE</h3>
                    <div className="row">

                      <div className="form-group col-sm-7">
                        <div className="inner-addon right-addon">
                          <label htmlFor="card-holder">Enfermedad</label>
                          <i className="far fa-user"></i>
                          {/* <input id="card-holder" type="text" className="form-control" placeholder="Card Holder" aria-label="Card Holder" aria-describedby="basic-addon1"/> */}
                          <Select
                            type="text"
                            id="card-holder"
                            className="basic-single"
                            aria-describedby="basic-addon1"
                            aria-label="Card Holder"
                            classNamePrefix="select"
                            defaultValue={enfOptions[0]}
                            isDisabled={false}
                            isLoading={false}
                            isClearable={true}
                            isRtl={false}
                            isSearchable={true}
                            name="Enfermedad"
                            options={enfOptions}
                            styles={customStyles}
                            onChange={(selectedOption) => setFormData({ ...formData, enfermedad: selectedOption.value })}
                          />
                        </div>	
                      </div>

                      <div className="form-group col-sm-5">
                        <label htmlFor="age-input">Edad</label>
                        <div className="input-group expiration-date">
                          <input
                            type="number"
                            className="form-control"
                            id="age-input"
                            placeholder="Edad"
                            aria-label="Edad"
                            aria-describedby="basic-addon1"
                            min="0"
                            max="120"
                            value={formData.edad}
                            onChange={handleInputChange}
                          />
                          <div className="input-group-append">
                            <span className="input-group-text">Años</span>
                          </div>
                        </div>
                      </div>

                      <div className="form-group col-sm-7">
                        <div className="inner-addon right-addon">
                          <label htmlFor="weight-input">Peso</label>
                          <i className="far fa-credit-card"></i>
                          <div className="input-group">
                            <input
                              id="weight-input"
                              type="number"
                              className="form-control"
                              placeholder="Peso"
                              aria-label="Peso"
                              aria-describedby="basic-addon1"
                              min="0"
                              step="0.1" // Cambia este valor según tus necesidades de precisión
                              value={formData.peso}
                              onChange={handleInputChange}
                            />
                            <div className="input-group-append">
                              <span className="input-group-text">Kg</span>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="form-group col-sm-5">
                        <label htmlFor="gender">Sexo</label>
                        <select id="gender" className="form-control" value={formData.sexo} onChange={handleInputChange}>
                          <option value="masculino">Masculino</option>
                          <option value="femenino">Femenino</option>
                        </select>
                      </div>

                      <div className="form-group col-sm-12">
                        <button type="submit" className="btn btn-primary btn-block">Introducir</button>
                      </div>
                      
                    </div>
                  </div>
                </form>				
              </div>{/* <!--/col-lg-6 -->  */}
            </div> {/* <!--/row --> */}
          </div>{/* <!--/card-box --> */}
        </div>{/* <!--/card-holder -->		  */}
      </div>{/* <!--/container --> */}
		</section>
    </>
  );
}

export default Formulario;
